import React, {useState} from 'react'
import {Segment, Image, Grid, Dimmer} from 'semantic-ui-react'

const Project = () => {
    const [active, setActive] = useState(true)
    const handleHide = () => {setActive(false)}
    const handShow = () => {setActive(true)}
    return(
        <>
            <Grid.Column>
                <Segment>
                    <Dimmer.Dimmable dimmed={active}>
                        <Image src="campaignquery.jpeg"/>
                        <Dimmer active={active} onMouseEnter={()=> handleHide()} onMouseLeave={()=> handShow()}/>
                    </Dimmer.Dimmable>
                </Segment>
            </Grid.Column>
        </>
    )
}

export default Project
